INSERT INTO `config_info` VALUES (3, 'zjm-common-prod.yml', 'DEFAULT_GROUP', 'spring:\n  datasource:\n    driver-class-name: com.mysql.cj.jdbc.Driver\n    url: jdbc:mysql://mysql:3306/zjmfz_mini_rig?characterEncoding=utf8&connectTimeout=1000&socketTimeout=3000&autoReconnect=true&useUnicode=true&useSSL=false&serverTimezone=Asia/Shanghai\n    username: root\n    password: $*nM2Kc4\n    hikari:\n      #适当调大，因使用虚拟线程，异步操作数据库时可达到更高并发，此处可以调高，适配虚拟线程，但数据库需要保证可以允许最大的连接数\n      maximum-pool-size: 100\n      minIdle: 10\n      connection-timeout: 30000\n      idle-timeout: 600000\n      max-lifetime: 1800000\n      # 连接有效性检查 \n      keepalive-time: 30000  # 每30秒发送保活包\n      connection-test-query: SELECT 1\n      validation-timeout: 5000\n  data:\n    redis:\n      host: redis\n      port: 6379\n      password: fUD5&b6%\n      database: 0\n      timeout: 3000ms\n      lettuce:\n        pool:\n          max-active: 20\n          max-wait: -1ms\n          max-idle: 8\n          min-idle: 0\nzjm:          \n  websocket:\n    path: /ws\n\noss:\n  endpoint: http://minio:9000\n  access-key: mini-rig\n  secret-key: minirigadmin\n  bucketName: mini-rig\n  http:\n    enable: true', '51508d99dc13c57e9c0719dd55b268c9', '2026-01-05 10:09:41', '2026-01-05 17:19:08', 'nacos', '172.18.0.1', '', 'zjmfz-mini-rig', '公共配置', NULL, NULL, 'yaml', NULL, '');
INSERT INTO `config_info` VALUES (4, 'gateway.yml', 'DEFAULT_GROUP', 'spring:\n  cloud:\n    loadbalancer:\n      cache:\n        enabled: true\n        caffeine:\n          spec: initialCapacity=500,expireAfterWrite=5s\n    gateway:\n      enabled: true\n      httpclient:\n        connect-timeout: 1000000\n        response-timeout: 1000000\n        pool:\n          max-connections: 5000\n          acquire-timeout: 15000\n      globalcors: # 全局的跨域处理\n        add-to-simple-url-handler-mapping: true # 解决options请求被拦截问题\n        corsConfigurations:\n          \'[/**]\':\n            allowedOriginPatterns: \"*\"\n            allowedMethods: # 允许的跨域ajax的请求方式\n              - \"GET\"\n              - \"POST\"\n              - \"DELETE\"\n              - \"PUT\"\n              - \"OPTIONS\"\n            allowedHeaders: \"*\" # 允许在请求中携带的头信息\n            allowCredentials: true # 是否允许携带cookie\n            maxAge: 360000\n      routes:\n        - id: avionics-simulation\n          uri: lb://avionics-simulation\n          order: 1\n          predicates:\n            - Path=/avionics-simulation/**\n          filters:\n            - StripPrefix=1\n        - id: test-rig\n          uri: lb://test-rig\n          order: 1\n          predicates:\n            - Path=/test-rig/**\n          filters:\n            - StripPrefix=1\n        - id: iot\n          uri: lb://iot\n          order: 1\n          predicates:\n            - Path=/iot/**\n          filters:\n            - StripPrefix=1\n\nknife4j:\n  #针对于gateway模块的配置\n  gateway:\n    enabled: true\n    # 排序规则(tag/operation排序自4.2.0版本新增)\n    # 取值：alpha-默认排序规则，官方swagger-ui默认实现,order-Knife4j提供的增强排序规则，开发者可扩展x-order，根据数值来自定义排序\n    tags-sorter: order\n    operations-sorter: order\n     # 指定手动配置的模式(默认为该模式manual) 服务发现(discover)\n    strategy: discover\n    discover:\n      enable: true\n      version: OpenAPI3\n      excluded-services:\n      #  - gateway\n      oas3:\n        url: /v3/api-docs?group=default\n        oauth2-redirect-url: \'\'\n        validator-url: \'\'\n      service-config:\n        avionics-simulation:\n          # 排序\n          order: 1\n          # 前端显示名称\n          group-name : 航电仿真\n          # 重新指定basePath，一般在OpenAPI3规范中需要\n          #context-path: /avionics-simulation\n        test-rig:\n          # 排序\n          order: 2\n          # 前端显示名称\n          group-name : 试验台', '0c11fbafb66ac7c282547b73bda53cac', '2026-01-04 16:15:09', '2026-01-04 16:15:09', 'nacos', '172.18.0.1', '', 'zjmfz-mini-rig', '网关相关配置', NULL, NULL, 'yaml', NULL, '');
