INSERT INTO `his_config_info` VALUES (0, 1, 'zjm-common.yml', 'DEFAULT_GROUP', '', 'spring:\n  threads:\n    virtual:\n      enabled: true\n  servlet:\n    multipart:\n      # 调整Spring Boot的文件上传限制\n      max-file-size: 500MB\n      max-request-size: 500MB\n      # 建议开启延迟解析，避免大文件占用过多内存\n      resolve-lazily: true\n  lifecycle:\n    timeout-per-shutdown-phase: 30s\n  cloud:\n    openfeign:\n      client:\n        config:\n          default: # 全局默认配置\n            # 连接超时时间（单位：毫秒）\n            connectTimeout: 5000\n            # 读取超时时间（单位：毫秒）\n            readTimeout: 10000\nfeign:\n  httpclient:\n   # 启用HttpClient\n    enabled: true\n     # 最大连接数\n    max-connections: 200\n     # 每个路由的最大连接数\n    max-connections-per-route: 50\n\n#监控\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \"health,info,metrics\" # * 暴露所有\n  endpoint:\n    health:\n      enabled: true\n    info:\n      enabled: true\n  health:\n    rabbit:\n      #禁用rabbitmq检查，自定义的starter使用的多rabbitmq连接，spring自动检查无法获取到对应地址，检查会一直失败\n      enabled: false\n\nlogging:\n  # 全局日志级别控制中心\n  level:\n    #整体日志级别\n    root: INFO\n    # 更细颗粒度控制，优先级高于root\n    #项目内部记录日志级别\n    com.zjm: debug \n    # com.baomidou.example.mapper: INFO\n    # com.alibaba.nacos: INFO\n    # com.baomidou.mybatisplus: INFO\n    # org.apache.ibatis: INFO\n    # org.springframework: INFO\n    # org.apache: INFO\n  # 文件路径\n  file:\n    path: ./logs\n  # 日志滚动策略\n  logback:\n    rollingpolicy:\n      max-file-size: 100MB\n      max-history: 30\n      total-size-cap: 3GB\n  # 日志格式\n  pattern:\n    # 控制台格式（使用彩色）\n    console: \"%d{yyyy-MM-dd HH:mm:ss.SSS} [%thread] %highlight(%-5level) %cyan(%logger{36}) - %msg%n\"\n    # 文件格式\n    file: \"%d{yyyy-MM-dd HH:mm:ss.SSS} [%thread] %-5level %logger{36} - %msg%n\"\n\n# MyBatis-Plus 专属配置\nmybatis-plus:\n  # 配置实体类（POJO）所在包的路径，MyBatis-Plus 会为其注册别名\n  type-aliases-package: com.zjm.**.dao.po\n  # 指定 Mapper 接口对应的 XML 映射文件的位置\n  mapper-locations: classpath*:mapper/**/*.xml\n  configuration:\n    # 开启/关闭自动驼峰命名规则映射（默认通常为true，可省略）\n    # 例如，数据库字段 `user_name` 会自动映射到实体类属性 `userName`\n    map-underscore-to-camel-case: true\n    # 在控制台打印执行的 SQL 语句及其参数，便于开发阶段调试\n    #log-impl: org.apache.ibatis.logging.stdout.StdOutImpl\n  global-config:\n    db-config:\n      # 主键生成策略。AUTO 表示使用数据库自增，其他选项如 ASSIGN_ID（雪花算法）\n      id-type: auto\n      # 表的前缀。如果所有表都有共同前缀（如 `t_`），设置后实体类的 `@TableName` 中可省略\n      # table-prefix: t_\n      # 逻辑删除配置（如果表中有类似 `deleted` 字段来实现逻辑删除）\n      # logic-delete-field: deleted  # 全局逻辑删除的实体字段名\n      # logic-delete-value: 1        # 逻辑已删除值（默认为 1）\n      # logic-not-delete-value: 0    # 逻辑未删除值（默认为 0）\n\n# springdoc-openapi项目配置\nspringdoc:\n  swagger-ui:\n    path: /swagger-ui.html\n    tags-sorter: alpha\n    operations-sorter: alpha\n  api-docs:\n    path: /v3/api-docs\n  group-configs:\n    - group: \'default\'\n      paths-to-match: \'/**\'\n      packages-to-scan: com.zjm.avionics.api.controller,com.zjm.rig.api.controller,com.zjm.cloud.gateway.auth.controller,com.zjm.rig.iot.controller\n      \n# knife4j的增强配置\nknife4j:\n  enable: true\n  setting:\n    language: zh_cn\n    swagger-model-name: zjmfz-mini-rig\n    enable-version: true\n    enable-footer: false\n\n############## Sa-Token 配置 (文档: https://sa-token.cc) ##############\nsa-token: \n    # 指定 token 提交时的前缀\n    token-prefix: Bearer\n    # token 名称（同时也是 cookie 名称）\n    token-name: satoken\n    # token 有效期（单位：秒） 默认30天，-1 代表永久有效\n    timeout: 2592000\n    # token 最低活跃频率（单位：秒），如果 token 超过此时间没有访问系统就会被冻结，默认-1 代表不限制，永不冻结\n    active-timeout: -1\n    # 是否允许同一账号多地同时登录 （为 true 时允许一起登录, 为 false 时新登录挤掉旧登录）\n    is-concurrent: true\n    # 在多人登录同一账号时，是否共用一个 token （为 true 时所有登录共用一个 token, 为 false 时每次登录新建一个 token）\n    is-share: false\n    # token 风格（默认可取值：uuid、simple-uuid、random-32、random-64、random-128、tik）\n    token-style: simple-uuid\n    # 是否输出操作日志 \n    is-log: true', '200d4d693956a52e1013acf845099ee6', '2026-01-04 16:10:53', '2026-01-04 16:10:53', 'nacos', '172.18.0.1', 'I', 'public', '', 'formal', '', '{\"src_user\":\"nacos\",\"type\":\"yaml\",\"c_desc\":\"公共配置\"}');
INSERT INTO `his_config_info` VALUES (0, 2, 'gateway.yml', 'DEFAULT_GROUP', '', 'spring:\n  cloud:\n    loadbalancer:\n      cache:\n        enabled: true\n        caffeine:\n          spec: initialCapacity=500,expireAfterWrite=5s\n    gateway:\n      enabled: true\n      httpclient:\n        connect-timeout: 1000000\n        response-timeout: 1000000\n        pool:\n          max-connections: 5000\n          acquire-timeout: 15000\n      globalcors: # 全局的跨域处理\n        add-to-simple-url-handler-mapping: true # 解决options请求被拦截问题\n        corsConfigurations:\n          \'[/**]\':\n            allowedOriginPatterns: \"*\"\n            allowedMethods: # 允许的跨域ajax的请求方式\n              - \"GET\"\n              - \"POST\"\n              - \"DELETE\"\n              - \"PUT\"\n              - \"OPTIONS\"\n            allowedHeaders: \"*\" # 允许在请求中携带的头信息\n            allowCredentials: true # 是否允许携带cookie\n            maxAge: 360000\n      routes:\n        - id: avionics-simulation\n          uri: lb://avionics-simulation\n          order: 1\n          predicates:\n            - Path=/avionics-simulation/**\n          filters:\n            - StripPrefix=1\n        - id: test-rig\n          uri: lb://test-rig\n          order: 1\n          predicates:\n            - Path=/test-rig/**\n          filters:\n            - StripPrefix=1\n        - id: iot\n          uri: lb://iot\n          order: 1\n          predicates:\n            - Path=/iot/**\n          filters:\n            - StripPrefix=1\n\nknife4j:\n  #针对于gateway模块的配置\n  gateway:\n    enabled: true\n    # 排序规则(tag/operation排序自4.2.0版本新增)\n    # 取值：alpha-默认排序规则，官方swagger-ui默认实现,order-Knife4j提供的增强排序规则，开发者可扩展x-order，根据数值来自定义排序\n    tags-sorter: order\n    operations-sorter: order\n     # 指定手动配置的模式(默认为该模式manual) 服务发现(discover)\n    strategy: discover\n    discover:\n      enable: true\n      version: OpenAPI3\n      excluded-services:\n      #  - gateway\n      oas3:\n        url: /v3/api-docs?group=default\n        oauth2-redirect-url: \'\'\n        validator-url: \'\'\n      service-config:\n        avionics-simulation:\n          # 排序\n          order: 1\n          # 前端显示名称\n          group-name : 航电仿真\n          # 重新指定basePath，一般在OpenAPI3规范中需要\n          #context-path: /avionics-simulation\n        test-rig:\n          # 排序\n          order: 2\n          # 前端显示名称\n          group-name : 试验台', '0c11fbafb66ac7c282547b73bda53cac', '2026-01-04 16:10:53', '2026-01-04 16:10:53', 'nacos', '172.18.0.1', 'I', 'public', '', 'formal', '', '{\"src_user\":\"nacos\",\"type\":\"yaml\",\"c_desc\":\"网关相关配置\"}');
INSERT INTO `his_config_info` VALUES (0, 3, 'iot.yml', 'DEFAULT_GROUP', '', 'zjm:\n  #rabbitmq配置\n  multi-rabbitmq:\n    instances:\n      primary: # 实例名称，自定义\n        host: 192.168.20.250\n        port: 5672\n        username: admin\n        password: admin\n        virtual-host: mini-rig\n        queues:\n          #下位机上报消息队列\n          MiniRig_Upstream_Queue:\n            name: MiniRig_Upstream_Queue\n            exchange: MiniRig_Upstream_Exchange\n            routing-key: MiniRig_Upstream_Routekey\n            exchange-type: fanout\n          #下发给下位机的消息队列\n          MiniRig_Downstream_Queue:\n            name: MiniRig_Downstream_Queue\n            exchange: MiniRig_Downstream_Exchange\n            routing-key: MiniRig_Downstream_Routekey\n            exchange-type: direct\n          #发送给业务端的消息\n          mini_rig_iot_msg_queue:\n            name: mini_rig_iot_msg_queue\n            exchange: mini_rig_iot_msg_exchange\n            routing-key: mini_rig_iot_msg_route\n            exchange-type: fanout\n\n\n  # netty相关配置\n  netty:\n    server-enabled: true\n    client-enabled: false\n    codec:\n      protocol:\n        protocol-type: JSON\n    udp-server:\n      enabled: true\n      port: 9082', '556ee78fc46e248a0d55e09f715f0dce', '2026-01-04 16:10:53', '2026-01-04 16:10:53', 'nacos', '172.18.0.1', 'I', 'public', '', 'formal', '', '{\"src_user\":\"nacos\",\"type\":\"yaml\"}');
