INSERT INTO `his_config_info` VALUES (0, 23, 'gateway-dev.yml', 'DEFAULT_GROUP', '', 'spring:\n  cloud:\n    loadbalancer:\n      cache:\n        enabled: true\n        caffeine:\n          spec: initialCapacity=500,expireAfterWrite=5s\n    gateway:\n      enabled: true\n      httpclient:\n        connect-timeout: 1000000\n        response-timeout: 1000000\n        pool:\n          max-connections: 5000\n          acquire-timeout: 15000\n      globalcors: # 全局的跨域处理\n        add-to-simple-url-handler-mapping: true # 解决options请求被拦截问题\n        corsConfigurations:\n          \'[/**]\':\n            allowedOriginPatterns: \"*\"\n            allowedMethods: # 允许的跨域ajax的请求方式\n              - \"GET\"\n              - \"POST\"\n              - \"DELETE\"\n              - \"PUT\"\n              - \"OPTIONS\"\n            allowedHeaders: \"*\" # 允许在请求中携带的头信息\n            allowCredentials: true # 是否允许携带cookie\n            maxAge: 360000\n      routes:\n        - id: avionics-simulation\n          uri: lb://avionics-simulation\n          order: 1\n          predicates:\n            - Path=/avionics-simulation/**\n          filters:\n            - StripPrefix=1\n        - id: test-rig\n          uri: lb://test-rig\n          order: 1\n          predicates:\n            - Path=/test-rig/**\n          filters:\n            - StripPrefix=1\n        - id: iot\n          uri: lb://iot\n          order: 1\n          predicates:\n            - Path=/iot/**\n          filters:\n            - StripPrefix=1\n\nknife4j:\n  #针对于gateway模块的配置\n  gateway:\n    enabled: true\n    # 排序规则(tag/operation排序自4.2.0版本新增)\n    # 取值：alpha-默认排序规则，官方swagger-ui默认实现,order-Knife4j提供的增强排序规则，开发者可扩展x-order，根据数值来自定义排序\n    tags-sorter: order\n    operations-sorter: order\n     # 指定手动配置的模式(默认为该模式manual) 服务发现(discover)\n    strategy: discover\n    discover:\n      enable: true\n      version: OpenAPI3\n      excluded-services:\n      #  - gateway\n      oas3:\n        url: /v3/api-docs?group=default\n        oauth2-redirect-url: \'\'\n        validator-url: \'\'\n      service-config:\n        avionics-simulation:\n          # 排序\n          order: 1\n          # 前端显示名称\n          group-name : 航电仿真\n          # 重新指定basePath，一般在OpenAPI3规范中需要\n          #context-path: /avionics-simulation\n        test-rig:\n          # 排序\n          order: 2\n          # 前端显示名称\n          group-name : 试验台', '0c11fbafb66ac7c282547b73bda53cac', '2026-01-05 10:10:30', '2026-01-05 02:10:31', 'nacos', '172.18.0.1', 'I', 'zjmfz-mini-rig', '', 'formal', '', '{\"src_user\":\"nacos\",\"type\":\"yaml\",\"c_desc\":\"网关相关配置\"}');
