INSERT INTO `his_config_info` VALUES (1, 48, 'zjm-common.yml', 'DEFAULT_GROUP', '', 'spring:\n  threads:\n    virtual:\n      enabled: true\n  servlet:\n    multipart:\n      # 调整Spring Boot的文件上传限制\n      max-file-size: 500MB\n      max-request-size: 500MB\n      # 建议开启延迟解析，避免大文件占用过多内存\n      resolve-lazily: true\n  lifecycle:\n    timeout-per-shutdown-phase: 30s\n  cloud:\n    openfeign:\n      client:\n        config:\n          default: # 全局默认配置\n            # 连接超时时间（单位：毫秒）\n            connectTimeout: 5000\n            # 读取超时时间（单位：毫秒）\n            readTimeout: 10000\nfeign:\n  httpclient:\n   # 启用HttpClient\n    enabled: true\n     # 最大连接数\n    max-connections: 200\n     # 每个路由的最大连接数\n    max-connections-per-route: 50\n\n#监控\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \"health,info,metrics\" # * 暴露所有\n  endpoint:\n    health:\n      enabled: true\n    info:\n      enabled: true\n  health:\n    rabbit:\n      #禁用rabbitmq检查，自定义的starter使用的多rabbitmq连接，spring自动检查无法获取到对应地址，检查会一直失败\n      enabled: false\n\nlogging:\n  # 全局日志级别控制中心\n  level:\n    #整体日志级别\n    root: INFO\n    # 更细颗粒度控制，优先级高于root\n    #项目内部记录日志级别\n    com.zjm: DEBUG \n    # com.baomidou.example.mapper: INFO\n    # com.alibaba.nacos: INFO\n    # com.baomidou.mybatisplus: INFO\n    # org.apache.ibatis: INFO\n    # org.springframework: INFO\n    # org.apache: INFO\n  # 文件路径\n  file:\n    path: ./logs\n  # 日志滚动策略\n  logback:\n    rollingpolicy:\n      max-file-size: 100MB\n      max-history: 30\n      total-size-cap: 3GB\n  # 日志格式\n  pattern:\n    # 控制台格式（使用彩色）\n    console: \"%d{yyyy-MM-dd HH:mm:ss.SSS} [%thread] %highlight(%-5level) %cyan(%logger{36}) - %msg%n\"\n    # 文件格式\n    file: \"%d{yyyy-MM-dd HH:mm:ss.SSS} [%thread] %-5level %logger{36} - %msg%n\"\n\n# MyBatis-Plus 专属配置\nmybatis-plus:\n  # 配置实体类（POJO）所在包的路径，MyBatis-Plus 会为其注册别名\n  type-aliases-package: com.zjm.**.dao.po\n  # 指定 Mapper 接口对应的 XML 映射文件的位置\n  mapper-locations: classpath*:mapper/**/*.xml\n  configuration:\n   